import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const FamilyForm = () => {
  const [kids, setKids] = useState([{ name: '', dob: '' }]);

  const handleAddKid = () => {
    setKids([...kids, { name: '', dob: '' }]);
  };

  const handleKidChange = (index, field, value) => {
    const newKids = [...kids];
    newKids[index][field] = value;
    setKids(newKids);
  };

  return (
    <form className="container mt-4">
      <div className="mb-3">
        <label className="form-label">Name:</label>
        <input type="text" className="form-control" name="name" />
      </div>
      <div className="mb-3">
        <label className="form-label">Phone Number:</label>
        <input type="text" className="form-control" name="phone" />
      </div>
      <div className="mb-3">
        <label className="form-label">Email:</label>
        <input type="email" className="form-control" name="email" />
      </div>
      <div className="mb-3">
        <label className="form-label">Spouse Name:</label>
        <input type="text" className="form-control" name="spouseName" />
      </div>
      <div className="mb-3">
        <label className="form-label">Kids:</label>
        {kids.map((kid, index) => (
          <div key={index} className="mb-3">
            <input
              type="text"
              className="form-control mb-2"
              placeholder="Kid's Name"
              value={kid.name}
              onChange={(e) => handleKidChange(index, 'name', e.target.value)}
            />
            <input
              type="date"
              className="form-control"
              placeholder="Kid's DOB"
              value={kid.dob}
              onChange={(e) => handleKidChange(index, 'dob', e.target.value)}
            />
          </div>
        ))}
        <button type="button" className="btn btn-primary" onClick={handleAddKid}>
          Add Kid
        </button>
      </div>
    </form>
  );
};

export default FamilyForm;
