import React, { useState } from 'react';

const FamilyForm = () => {
  const [kids, setKids] = useState([{ name: '', dob: '' }]);

  const handleAddKid = () => {
    setKids([...kids, { name: '', dob: '' }]);
  };

  const handleKidChange = (index, field, value) => {
    const newKids = [...kids];
    newKids[index][field] = value;
    setKids(newKids);
  };

  return (
    <form>
      <div>
        <label>Name:</label>
        <input type="text" name="name" />
      </div>
      <div>
        <label>Phone Number:</label>
        <input type="text" name="phone" />
      </div>
      <div>
        <label>Email:</label>
        <input type="email" name="email" />
      </div>
      <div>
        <label>Spouse Name:</label>
        <input type="text" name="spouseName" />
      </div>
      <div>
        <label>Kids:</label>
        {kids.map((kid, index) => (
          <div key={index}>
            <input
              type="text"
              placeholder="Kid's Name"
              value={kid.name}
              onChange={(e) => handleKidChange(index, 'name', e.target.value)}
            />
            <input
              type="date"
              placeholder="Kid's DOB"
              value={kid.dob}
              onChange={(e) => handleKidChange(index, 'dob', e.target.value)}
            />
          </div>
        ))}
        <button type="button" onClick={handleAddKid}>
          Add Kid
        </button>
      </div>
    </form>
  );
};

export default FamilyForm;
