import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { FaPlus } from 'react-icons/fa';

const FamilyForm = () => {
  const [kids, setKids] = useState([{ name: '', dob: '' }]);

  const handleAddKid = () => {
    setKids([...kids, { name: '', dob: '' }]);
  };

  const handleKidChange = (index, field, value) => {
    const newKids = [...kids];
    newKids[index][field] = value;
    setKids(newKids);
  };

  const handleDeleteKid = (index) => {
    const newKids = kids.filter((_, i) => i !== index);
    setKids(newKids);
  };

  const validateEmail = (email) => {
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(String(email).toLowerCase());
  };

  const validatePhoneNumber = (phone) => {
    const re = /^\d{10}$/;
    return re.test(String(phone));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const name = e.target.name.value;
    const email = e.target.email.value;
    const phone = e.target.phone.value;

    if (!validateEmail(email)) {
      alert("Invalid email address");
      return;
    }

    if (!validatePhoneNumber(phone)) {
      alert("Invalid phone number");
      return;
    }

    const formData = {
      name: name,
      phone: phone,
      email: email,
      spouseName: e.target.spouseName.value,
      kids: kids
    };

    console.log('Form submitted:', formData);
    alert('Form submitted successfully');

    fetch("https://qr-code-generator-wr3i.onrender.com/generate-pass", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ name: name, url: "https://www.midsouthtamilsangam.org/" })
    })
    .then(response => response.json())
    .then(data => {
      console.log("PassSlot API Response:", data);
      if (data.url) {
        const qrcodeContainer = document.getElementById("qrcodeContainer");
        const addToWalletButton = document.createElement("button");
        addToWalletButton.className = "addToWallet btn btn-primary";
        addToWalletButton.onclick = () => window.location.href = data.url;
        addToWalletButton.textContent = "Add to Apple Wallet";
        qrcodeContainer.innerHTML = '';
        qrcodeContainer.appendChild(addToWalletButton);
      } else {
        console.log("Failed to create Apple Wallet pass");
      }
    })
    .catch(error => console.error("Fetch Error:", error));
  };

  return (
    <form className="container mt-4" onSubmit={handleSubmit}>
      <h2 className="mb-4">Register Details</h2>
      <div className="row mb-3">
        <div className="col-md-12">
          <input type="text" className="form-control mb-2" name="name" placeholder="Name" />
          <input type="text" className="form-control mb-2" name="phone" placeholder="Phone Number" />
          <input type="email" className="form-control mb-2" name="email" placeholder="Email" />
          <input type="text" className="form-control mb-2" name="spouseName" placeholder="Spouse Name" />
        </div>
      </div>
      <div className="mb-3">
        {kids.map((kid, index) => (
          <div key={index} className="row mb-3">
            <div className="col-md-5">
              <input
                type="text"
                className="form-control mb-2"
                placeholder="Kid's Name"
                value={kid.name}
                onChange={(e) => handleKidChange(index, 'name', e.target.value)}
              />
            </div>
            <div className="col-md-5">
              <input
                type="date"
                className="form-control"
                placeholder="Kid's DOB"
                value={kid.dob}
                onChange={(e) => handleKidChange(index, 'dob', e.target.value)}
              />
            </div>
            <div className="col-md-2">
              <button
                type="button"
                className="btn btn-danger"
                onClick={() => handleDeleteKid(index)}
              >
                Remove
              </button>
            </div>
          </div>
        ))}
        <button type="button" className="btn btn-primary" onClick={handleAddKid}>
          <FaPlus /> Kids
        </button>
      </div>
      <div id="qrcodeContainer" className="text-center">
        <button type="submit" className="btn btn-success">Submit</button>
      </div>
    </form>
  );
};

export default FamilyForm;
